import { Component } from '@angular/core';
import { HttpClient, HttpResponse } from '@angular/common/http';

import { Employee } from '../Entity/Employee';
import { Observable } from 'rxjs';
@Component({
  selector: 'app-update',
  templateUrl: './update.component.html',
  styleUrls: ['./update.component.css']
})
export class UpdateComponent {

  baseURL: string = "http://localhost:8080/crud";

  constructor(private http: HttpClient) {
  }
  employee:any;
  searchInput = '';
  empName = '';
  empSalary = '';
  empAge = '';
  empRole = '';
  update() {
    const headers = { 'content-type': 'application/json'};


    this.http
    .post(this.baseURL +"/update",{employee_Name:this.empName,employee_Salary:this.empSalary,employee_Age:this.empAge,employee_Role:this.empRole},{'headers':headers})
    .subscribe((Response) =>{
      console.log(Response)
    },Error =>{console.log(Error)})


  }


  search() {
    const headers = { 'content-type': 'application/json'};

    console.log(this.empName)
    this.http
    .get(this.baseURL+"/search?empName="+this.searchInput,{'headers':headers})
    .subscribe((employee:any)=>{
      this.employee = employee;
     console.log(this.employee);
    })


  }

}
<div class="update-content">

    <div class="search-content">
        <div class="form__group">
            <input type="text" id="name" class="form__field" [(ngModel)]="searchInput" placeholder="Employee Name">
            <label for="name" class="form__label">Employee Name</label>

            <button class="btn btn-danger" style="margin-bottom: 10px;" (click)="search()">Search</button>
        </div>
    </div>

    <div class="form__group">
        <input type="text" id="name" class="form__field" [(ngModel)]="empName" placeholder="Employee Name">
        <label for="name" class="form__label">Employee Name</label>
    </div>
    <div class="form__group">
        <input type="number" id="salary" class="form__field" [(ngModel)]="empSalary" placeholder="Employee Salary">
        <label for="salary" class="form__label">Employee Salary</label>
    </div>
    <div class="form__group">
        <input type="number" id="age" class="form__field" [(ngModel)]="empAge" placeholder="Employee Age">
        <label for="age" class="form__label">Employee Age</label>
    </div>
    <div class="form__group">
        <input type="text" id="role" class="form__field" [(ngModel)]="empRole" placeholder="Employee Role">
        <label for="role" class="form__label">Employee Role</label>
    </div>





</div>
<button class="btn btn-info">Update</button>
